import { Services } from "./Services"
import Constants from "../Constants"


const getLiveAuctionListAction = () => {
    return (Dispatch) => {
        debugger
        Dispatch({ type: Constants.GET_LIVE_AUCTION_LIST_REQUEST, })
        Services.getService('auction', details).then(
            (response) => {
                if (response.success) { return Dispatch({ type: Constants.GET_LIVE_AUCTION_LIST_REQUEST, data: response.data }) }
                else { return Dispatch({ type: Constants.GET_LIVE_AUCTION_LIST_SUCESS, data: response }) }
            },
            (error) => { return Dispatch({ type: Constants.GET_LIVE_AUCTION_LIST_FAILED, data: error }) }
        )
    }
}

const getSoldAuctionListAction = (details) => {
    return (Dispatch) => {
        Dispatch({ type: Constants.GET_SOLD_AUCTION_LIST_REQUEST, })
        Services.postService('auction', details).then(
            (response) => {
                if (response.success) { return Dispatch({ type: Constants.GET_SOLD_AUCTION_LIST_REQUEST, data: response.data }) }
                else { return Dispatch({ type: Constants.GET_SOLD_AUCTION_LIST_SUCESS, data: response }) }
            },
            (error) => { return Dispatch({ type: Constants.GET_SOLD_AUCTION_LIST_FAILED, data: error }) }
        )
    }
}
const getExpiredAuctionListAction = (details) => {
    return (Dispatch) => {
        Dispatch({ type: Constants.GET_EXPIRED_AUCTION_LIST_REQUEST, })
        Services.postService('auction', details).then(
            (response) => {
                if (response.success) { return Dispatch({ type: Constants.GET_EXPIRED_AUCTION_LIST_REQUEST, data: response.data }) }
                else { return Dispatch({ type: Constants.GET_EXPIRED_AUCTION_LIST_SUCESS, data: response }) }
            },
            (error) => { return Dispatch({ type: Constants.GET_EXPIRED_AUCTION_LIST_FAILED, data: error }) }
        )
    }
}

export {
    getLiveAuctionListAction,
    getSoldAuctionListAction,
    getExpiredAuctionListAction,
}
