import Constants from '../Constants'
export const getAllSkill = (state = {}, action) => {
    switch (action.type) {
        case Constants.GET_ALL_SKIL_REQUEST: {
            return { ...state, Loading: true }
        }
        case Constants.GET_ALL_SKIL_SUCESS: {
            return { ...state, skillsList: action.data, Loading: false }
        }
        case Constants.GET_ALL_SKIL_FAILED: {
            return { ...state, Error: '', Loading: false }
        }
        default:
            return { ...state }
    }
}

export const addSkill = (state = {}, action) => {
    switch (action.type) {
        case Constants.ADD_SKIL_REQUEST: {
            return { ...state, Loading: true }
        }
        case Constants.ADD_SKIL_SUCESS: {
            return { ...state, newSkillAdded: action.data, Loading: false }
        }
        case Constants.ADD_SKIL_FAILED: {
            return { ...state, Error: '', Loading: false }
        }
        default:
            return { ...state }
    }
}

export const updateSkill = (state = {}, action) => {
    switch (action.type) {
        case Constants.UPDATE_SKIL_REQUEST: {
            return { ...state, Loading: true }
        }
        case Constants.UPDATE_SKIL_SUCESS: {
            return { ...state, skillUpdated: action.data, Loading: false }
        }
        case Constants.UPDATE_SKIL_FAILED: {
            return { ...state, Error: '', Loading: false }
        }
        default:
            return { ...state }
    }
}

export const removedSkill = (state = {}, action) => {
    switch (action.type) {
        case Constants.REMOVE_SKIL_REQUEST: {
            return { ...state, Loading: true }
        }
        case Constants.REMOVE_SKIL_SUCESS: {
            return { ...state, skillRemoved: action.data, Loading: false }
        }
        case Constants.REMOVE_SKIL_FAILED: {
            return { ...state, Error: '', Loading: false }
        }
        default:
            return { ...state }
    }
}
