import Constants from '../Constants'
export const getAuctionListAction = (state = {}, action) => {
    switch (action.type) {
        case Constants.GTE_AUCTION_LIST_REQUEST: {
            return { ...state, Loading: true }
        }
        case Constants.GTE_AUCTION_LIST_REQUEST: {
            return { ...state, liveAuctionList: action.data, Loading: false }
        }
        case Constants.GTE_AUCTION_LIST_REQUEST: {
            return { ...state, Error: '', Loading: false }
        }
        default:
            return { ...state }
    }
}
