import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import HeaderComponent from "./HeaderComponent";
import { getLiveAuctionListAction } from '../Redux/Actions/Actions'
function LotsComponent(props) {

   

    console.log(liveAuctioList);
    return (
        <>
            <HeaderComponent />
            <div className="border-light card shadow" >
                <div className="alert alert-default alert-dismissible fade show" role="alert">
                    <span className="alert-inner--text"><strong>Good Evening!</strong> Test USer 1 from Max Steel Company You have
                        the best sense of style.</span>
                    <button type="button" className="close" data-dismiss="alert" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                    </button>
                </div>
                <div className="row justify-content-md-center text-center">
                    {lotData && lotData.map((lot, index) =>
                        <div className="card col-lg-5 col-md-5 col-sm-12 p-0 m-2 border-light" key={index}>
                            <div className="card-header p-0">
                                <div className="card-title bg-gradient-lighter h6 mb-1">{lot.title}</div>
                                <div className="">{lot.company}</div>
                            </div>
                            <div className="card-body p-0">
                                <button type="button" className="btn btn-sm btn-primary btn-tooltip">{lot.lotAt}</button>
                                <div className="border-bottom pb-1">
                                    <div className="btn btn-sm btn-secondary btn-tooltip" >Total Quentity : {lot.qty}</div>
                                    <button type="button" className="btn btn-sm btn-secondary btn-tooltip" >Lots : {lot.lots}</button>
                                </div>
                                <div className="border-bottom pb-1">
                                    <button type="button" className="btn btn-sm btn-secondary btn-tooltip" > Date : {lot.date} </button>
                                    <button type="button" className="btn btn-sm btn-secondary btn-tooltip" >From :{lot.start}
                                        10:00:00 pm</button>
                                    <button type="button" className="btn btn-sm btn-secondary btn-tooltip" >Till :{lot.end}
                                        12:00:00 pm</button>
                                </div>
                                <div className="border-bottom pb-1">
                                    {lot.others && lot.others.map((other, index) =>
                                        <button type="button" className="btn btn-sm btn-secondary btn-tooltip" key={index}>{other}</button>
                                    )}
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </>)

}
const mapStateToProps = (state) => {
    return {
        liveAuctionList: state.getLiveAuctionListAction.liveAuctionList,
    }
}
const mapDispatchtoProps = {
    getLiveAuctionList: () => getLiveAuctionListAction(),
}
export default connect(mapStateToProps, mapDispatchtoProps)(LotsComponent)
