import axios from 'axios'
import Constants from '../Constants'

const getService = (endPoint) => {
    return axios.get(Constants.BASEURL + endPoint)
        .then(Response => Response.data).catch(error => { return error.response });
}

const getByIdService = (endPoint, para) => {
    debugger
    return axios.get(Constants.BASEURL + endPoint + '/' + para)
        .then(Response => Response.data).catch(error => { return error.response });
}

const postService = (endPoint, para) => {
    return axios.post(Constants.BASEURL + endPoint, para)
        .then(Response => Response.data).catch(error => { return error.response });
}
const patchService = (endPoint, id, para) => {
    return axios.patch(Constants.BASEURL + endPoint + '/' + id, para)
        .then(Response => Response.data).catch(error => { return error.response });
}

const deleteService = (endPoint, para) => {
    return axios.delete(Constants.BASEURL + endPoint + '/' + para)
        .then(Response => Response.data).catch(error => { return error.response });
}

export const Services = {
    getService,
    getByIdService,
    postService,
    patchService,
    deleteService,
}
