import { Services } from "./Services"
import Constants from "../Constants"


const getLiveAuctionListAction = (details) => {
    return (Dispatch) => {
        Dispatch({ type: Constants.GET_LIVE_AUCTION_LIST_REQUEST, })
        Services.getByIdService('auction', '?type=current').then(
            (response) => {
                if (response.auction) {
                    return Dispatch({ type: Constants.GET_LIVE_AUCTION_LIST_SUCESS, data: response })
                }
                else { return Dispatch({ type: Constants.GET_LIVE_AUCTION_LIST_SUCESS, data: response }) }
            },
            (error) => { return Dispatch({ type: Constants.GET_LIVE_AUCTION_LIST_FAILED, data: error }) }
        )
    }
}

const getUpCommingAuctionListAction = (details) => {
    return (Dispatch) => {
        Dispatch({ type: Constants.GET_UPCOMMING_AUCTION_LIST_REQUEST, })
        Services.getByIdService('auction', '?type=upcoming').then(
            (response) => {
                if (response.auction) {
                    return Dispatch({ type: Constants.GET_UPCOMMING_AUCTION_LIST_SUCESS, data: response })
                }
                else { return Dispatch({ type: Constants.GET_UPCOMMING_AUCTION_LIST_FAILED, data: response }) }
            },
            (error) => { return Dispatch({ type: Constants.GET_UPCOMMING_AUCTION_LIST_FAILED, data: error }) }
        )
    }
}

const getSoldAuctionListAction = (details) => {
    return (Dispatch) => {
        Dispatch({ type: Constants.GET_SOLD_AUCTION_LIST_REQUEST, })
        Services.getByIdServiceS('auction', '?type=soled').then(
            (response) => {
                if (response.auction) { return Dispatch({ type: Constants.GET_SOLD_AUCTION_LIST_SUCESS, data: response }) }
                else { return Dispatch({ type: Constants.GET_SOLD_AUCTION_LIST_FAILED, data: response }) }
            },
            (error) => { return Dispatch({ type: Constants.GET_SOLD_AUCTION_LIST_FAILED, data: error }) }
        )
    }
}

const getExpiredAuctionListAction = (details) => {
    return (Dispatch) => {
        Dispatch({ type: Constants.GET_EXPIRED_AUCTION_LIST_REQUEST, })
        Services.getByIdServiceS('auction', '?type=expired').then(
            (response) => {
                if (response.auction) { return Dispatch({ type: Constants.GET_EXPIRED_AUCTION_LIST_SUCESS, data: response }) }
                else { return Dispatch({ type: Constants.GET_EXPIRED_AUCTION_LIST_FAILED, data: response }) }
            },
            (error) => { return Dispatch({ type: Constants.GET_EXPIRED_AUCTION_LIST_FAILED, data: error }) }
        )
    }
}

const getLotsAction = (details) => {
    return (Dispatch) => {
        Dispatch({ type: Constants.GET_LOTS_LIST_REQUEST, })
        Services.getService('lots').then(
            (response) => {
                if (response) {
                    return Dispatch({ type: Constants.GET_LOTS_LIST_SUCESS, data: response })
                }
                else { return Dispatch({ type: Constants.GET_LOTS_LIST_FAILED, data: response }) }
            },
            (error) => { return Dispatch({ type: Constants.GET_LOTS_LIST_FAILED, data: error }) }
        )
    }
}


const SignUpAction = (details) => {
    return (Dispatch) => {
        Dispatch({ type: Constants.GET_LOTS_LIST_REQUEST, })
        Services.postService('lots').then(
            (response) => {
                if (response) {
                    return Dispatch({ type: Constants.GET_LOTS_LIST_SUCESS, data: response })
                }
                else { return Dispatch({ type: Constants.GET_LOTS_LIST_FAILED, data: response }) }
            },
            (error) => { return Dispatch({ type: Constants.GET_LOTS_LIST_FAILED, data: error }) }
        )
    }
}

export {
    getLiveAuctionListAction,
    getSoldAuctionListAction,
    getExpiredAuctionListAction,
    getLotsAction,
    getUpCommingAuctionListAction,
}
