import Constants from '../Constants'

export const getLiveAuctionListReducer = (state = {}, action) => {
    switch (action.type) {
        case Constants.GET_LIVE_AUCTION_LIST_REQUEST: {
            return { ...state, Loading: true }
        }
        case Constants.GET_LIVE_AUCTION_LIST_SUCESS: {
            return { ...state, liveAuctionList: action.data, Loading: false }
        }
        case Constants.GET_LIVE_AUCTION_LIST_FAILED: {
            return { ...state, Error: action, Loading: false }
        }
        default:
            return { ...state }
    }
}

export const getSoldAuctionListReducer = (state = {}, action) => {
    switch (action.type) {
        case Constants.GET_SOLD_AUCTION_LIST_REQUEST: {
            return { ...state, Loading: true }
        }
        case Constants.GET_SOLD_AUCTION_LIST_SUCESS: {
            return { ...state, soldAuctionList: action.data, Loading: false }
        }
        case Constants.GET_SOLD_AUCTION_LIST_FAILED: {
            return { ...state, Error: action, Loading: false }
        }
        default:
            return { ...state }
    }
}

export const getExpiredAuctionListReducer = (state = {}, action) => {
    switch (action.type) {
        case Constants.GET_EXPIRED_AUCTION_LIST_REQUEST: {
            return { ...state, Loading: true }
        }
        case Constants.GET_EXPIRED_AUCTION_LIST_SUCESS: {
            return { ...state, expiredAuctionList: action.data, Loading: false }
        }
        case Constants.GET_EXPIRED_AUCTION_LIST_FAILED: {
            return { ...state, Error: action, Loading: false }
        }
        default:
            return { ...state }
    }
}

export const getLotsListReducer = (state = {}, action) => {
    switch (action.type) {
        case Constants.GET_LOTS_LIST_REQUEST: {
            return { ...state, Loading: true }
        }
        case Constants.GET_LOTS_LIST_SUCESS: {
            return { ...state, lotsList: action.data.lots, Loading: false }
        }
        case Constants.GET_LOTS_LIST_FAILED: {
            return { ...state, Error: action, Loading: false }
        }
        default:
            return { ...state }
    }
}

export const getUpCommingAuctionListReducer = (state = {}, action) => {
    switch (action.type) {
        case Constants.GET_UPCOMMING_AUCTION_LIST_REQUEST: {
            return { ...state, Loading: true }
        }
        case Constants.GET_UPCOMMING_AUCTION_LIST_SUCESS: {
            return { ...state, upCommingAuctionList: action.data.auction, Loading: false }
        }
        case Constants.GET_UPCOMMING_AUCTION_LIST_FAILED: {
            return { ...state, Error: action, Loading: false }
        }
        default:
            return { ...state }
    }
}

export const SignUpAction = (state = {}, action) => {
    switch (action.type) {
        case Constants.GET_UPCOMMING_AUCTION_LIST_REQUEST: {
            return { ...state, Loading: true }
        }
        case Constants.GET_UPCOMMING_AUCTION_LIST_SUCESS: {
            return { ...state, upCommingAuctionList: action.data.auction, Loading: false }
        }
        case Constants.GET_UPCOMMING_AUCTION_LIST_FAILED: {
            return { ...state, Error: action, Loading: false }
        }
        default:
            return { ...state }
    }
}
